let Declaration = require('../declaration')

class MaskBorder extends Declaration {
  /**
   * Return property name by final spec
   */
  normalize() {
    return this.name.replace('box-image', 'border')
  }

  /**
   * Return flex property for 2012 spec
   */
  prefixed(prop, prefix) {
    let result = super.prefixed(prop, prefix)
    if (prefix === '-webkit-') {
      result = result.replace('border', 'box-image')
    }
    return result
  }
}

MaskBorder.names = [
  'maREMOVEDborder',
  'maREMOVEDborder-source',
  'maREMOVEDborder-slice',
  'maREMOVEDborder-width',
  'maREMOVEDborder-outset',
  'maREMOVEDborder-repeat',
  'maREMOVEDbox-image',
  'maREMOVEDbox-image-source',
  'maREMOVEDbox-image-slice',
  'maREMOVEDbox-image-width',
  'maREMOVEDbox-image-outset',
  'maREMOVEDbox-image-repeat'
]

module.exports = MaskBorder
